$(function(){var n=[];$.ajax({type:"POST",url:"Request.aspx/GetLocations",contentType:"application/json; charset=utf-8",dataType:"json",success:function(t){n=JSON.parse(t.d);var i=$.map(n,function(n){return n.Name});$("#txtLocationAutoCompleteBox").autocomplete({source:i,select:function(t,i){for(var r=0;r<n.length;r++)if(n[r].Name==i.item.value){$("#hfLocation").val(n[r].ID);break}}})}})})